mapper.select:
select
from t_disease_case
order by modify_time desc limit #{pagination.start}, #{pagination.pageSize}

mapper.update:
update t_disease_case
set is_delete = 1, modify_by=#{modifyBy}, modify_time = now()
where id = #{id}

mapper.sql:
where 1=1

  and instr(depart_name,#{departName,jdbcType=VARCHAR})>0


  id = #{id}


  and instr(name,#{name,jdbcType=VARCHAR})>0


  and  age >= #{ageStart} 


  and  age <= #{ageEnd} 


    sex = #{sex}


  and  in_hospital_date >= #{sbpStart} 


  and  out_hospital_date <= #{endDate} 


  and instr(in_diagnose,#{inDiagnose,jdbcType=VARCHAR})>0


  and instr(out_diagnose,#{outDiagnose,jdbcType=VARCHAR})>0


  and  physical_examine4 >= #{sbpStart} 


  and  physical_examine4 <= #{sbpEnd} 


  and  physical_examine5 >= #{dbpStart} 


  and  physical_examine5 <= #{dbpEnd} 


  and  physical_examine6 >= #{heightStart} 


  and  physical_examine6 <= #{heightEnd} 


  and  physical_examine7 >= #{weightStart} 


  and  physical_examine7 <= #{weightEnd} 


  and  blood_routine5 >= #{hgbStart} 


  and  blood_routine5 <= #{hgbEnd} 


  and  blood_routine25 >= #{routine25Start} 


  and  blood_routine25 <= #{routine25End} 


  and  blood_routine26 >= #{routine26Start} 


  and  blood_routine26 <= #{routine26End} 


  and  blood_routine27 >= #{routine27Start} 


  and  blood_routine27 <= #{routine27End} 


  and  blood_routine28 >= #{routine28Start} 


  and  blood_routine28 <= #{routine28End} 


  and  blood_routine33 >= #{routine33Start} 


  and  blood_routine33 <= #{routine33End} 


  and  blood_routine36 >= #{routine36Start} 


  and  blood_routine36 <= #{routine36End} 


  and  blood_routine37 >= #{routine37Start} 


  and  blood_routine37 <= #{routine37End} 


  and  blood_routine38 >= #{routine38Start} 


  and  blood_routine38 <= #{routine38End} 


  and  blood_routine39 >= #{routine39Start} 


  and  blood_routine39 <= #{routine39End} 


  and  blood_routine47 >= #{routine47Start} 


